#define tex(x,y) texture(iChannel0,(vec2(x,y)+.5)/R)
#define B 5./R                                               // border size
vec2 R;

#define activeWindow() tex(0,0).y                            // wich window is active ?
vec4 drawWindow( vec2 U, int c );

vec4 WM(vec2 U) {                    // -------------- display windows ----------------
    for (int i=1; i<=16; i++) 
        if (i<=int(tex(0,0).x)) {
            vec4 W = tex(i,0);
            vec2 In = step( abs(U-W.xy-W.zw/2.), W.zw/2.);  
            if (In.x*In.y>0.) {                              // pixel in window #i 
                U = (U-W.xy)/W.zw;
                int c = int(tex(i,1).x);                     // channel attached to window
                return drawWindow(  U,  c ); 
            }
            In = step( abs(U-W.xy-W.zw/2.), W.zw/2.+B);
            if (In.x*In.y>0.)                                // pixel in window #i border
                return int(activeWindow())==i ? vec4(1.) : vec4(.5,.5,.5,1);  
        }
    return vec4(0);
}

vec4 drawWindow( vec2 U, int c ) // -------------- your window content here ------------
{
    if      (c==1) return texture(iChannel1,U);
    else if (c==2) return texture(iChannel2,U);
    else if (c==3) return texture(iChannel3,U);    
    else return vec4(0);
}

void mainImage( out vec4 O,  vec2 U )
{ 
    R = iResolution.xy;
    O = WM(U/R);                         // display windows
    O = mix (vec4(.1,.1,.5,1), O, O.a);  // blend background
}

#define window(x,y,l,h,c) { N++; if (U==vec2(N,0)) O=vec4(x,y,l,h); if (U==vec2(N,1)) O=vec4(c,0,0,0);} 
#define tex(U) texture(iChannel0,(U+.5)/R)
#define B 5./R                                               // border size

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    U -= .5;

    if (iFrame==0) {
        float N=0.;      // --- declare your windows here : x,y (BottomLeft) w,h,  channel
        window(.1,.1, .4,.3, 1);
        window(.4,.3, .5,.4, 2);
        window(.03,.6, .5,.3, 3);
        if (U==vec2(0)) O.x=N;
        return;
    }

    O = tex(U);  

    if (U.y==0.)
    if (iMouse.z<=0.)  
       { if (U.x==0.) O.y = 0.; }                                    // no window selected
    else                                                             // click
        if (U.x==0.) {
            O.zw = iMouse.xy/R;
            if (O.y==0.) {                                           // nothing selected
                for (int i=1; i<=16; i++) 
                  if (i<=int(tex(vec2(0)).x)) {
                      vec4 W = tex(vec2(i,0));
                      vec2 M = iMouse.xy/R;
                      vec2 In = step( abs(M-W.xy-W.zw/2.), W.zw/2.+B);
                      if (In.x*In.y>0.) { O.y = float(i); return; } // select window
                }
            }

        }
    else if (U.x==tex(vec2(0)).y) {                                 // move selected window
            vec4 W = tex(vec2(U.x,0));
            vec2 M = iMouse.xy/R, dM = M.xy-tex(vec2(0)).zw;
            vec2 In = step( abs(M-W.xy-W.zw/2.), W.zw/2.-B);
            if (In.x*In.y>0.)                                       // pixel in window #i
                { O.xy += dM; return; } 
        	vec2 P = (M-W.xy-W.zw/2.)/(W.zw/2.+B);
            In = step( abs(P), vec2(1));
            if (In.x*In.y>0.) {                                     // pixel in window #i border
                if (P.x>.5) O.z += dM.x;   
                if (P.y>.5) O.w += dM.y;   
                if (P.x<-.5) { O.x += dM.x; O.z -= dM.x;  }
                if (P.y<-.5) { O.y += dM.y; O.w -= dM.y;  }
            }

        }
}
