float Circle(vec2 uv, vec2 pos, float r){

    float d = length(uv - pos);
    return r / d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    float r = .25;

    uv.x *= iResolution.x / iResolution.y;

    float X = uv.x * 6. + iTime;
    float Y = uv.y * 6. + iTime;

    uv.x += cos(X + Y) * 0.01 * cos(Y);
    uv.y += sin(X + Y) * 0.01 * sin(Y);

    // eyes
    float c = Circle(uv, vec2(-.25, 0.1), r);
    c += Circle(uv, vec2(.25, 0.1), r);


    uv.x *= .6;
    uv.y *= 3.;
    float n = Circle(uv, vec2(.0, -0.3), r * .25);

    c += n;



    // Output to screen
    fragColor = vec4(vec3(0.), 1.) + vec4(0.1 * c, .1 * c, .8 * c, 1.) * c;
}
