#define time iTime
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float circle(vec2 uv){
    return smoothstep(0.1,0.09,length(uv))-smoothstep(0.09,0.08,length(uv));
}

float body(vec2 st,float pos){
    float k;
    vec2 uv = st;
    st = vec2(st.x*1.7,st.y)-vec2(pos*1.7,0.6-sin(time*3.+(pos*3.)+0.5)*0.1);

    if (st.x>0.2 && st.x <0.8){

    k = smoothstep(0.4,0.38,st.y)-smoothstep(0.6,0.5,st.y);
    }
    return k;
}

vec4 tire(vec2 st,float pos){
    vec2 lst = vec2(st.x*1.7,st.y)-vec2(pos*1.7,0.6-sin(time*3.+pos*3.)*0.1);
    float c = circle(lst);


    float r = 0.08+0.03*cos(atan(lst.y,lst.x)*10.+time*23.);
    float circle = smoothstep(r,r-0.05,length(lst));

    r = 0.05;
    st.y+= -0.3;
    float cl=circle;
    return vec4(c)+vec4(cl)*vec4(1.,0.,0.,0.);

    }
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv;


    uv.y+= sin(uv.x*3.+time*3.)*0.1;


    float col = 1.-smoothstep(0.5,0.52,uv.y);
    vec3 terrain = vec3(0.1,col*0.7,0.1);
    vec4 tir = tire(st,0.3);
    tir += tire(st,0.5);
    //tir += vec4(body(st+vec2(0.0,0.5),0.0))*vec4(0.2,1.3,0.3,1.);
    vec3 back = vec3(0.0,0.0,1.)*texture(iChannel0,st).r;
    fragColor = mix(vec4(terrain,1.)+tir,vec4(back,1.),1.-uv.y);
     st -= vec2(0.3,0.6-0.29-sin(time*3.+(0.3*3.)+0.5)*0.1);

    st = rotate2d( (sin(time*3.+(0.3*3.)+0.5)*0.1)/3. ) * st;

    st += vec2(0.3,0.60-.29-sin(time*3.+(0.3*3.)+0.5)*0.1);
    fragColor += vec4(body(st+vec2(0.0,0.29),0.1))*vec4(0.2,1.3,0.3,1.);

    }
