void example1( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 c = iTime + uv.xyx + vec3(0,2,4);
    vec3 col = 0.5 + 0.5 * cos(c);
    fragColor = vec4(col,1.0);
}

void example2( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = cos(uv.xyx);
    fragColor = vec4(col, 1.0);
}

void example3( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(uv.xyx, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	example1(fragColor, fragCoord);
