void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 grad = 8.0 *
        vec2(texture(iChannel0, uv + vec2(0.125, 0.0)).x -
             texture(iChannel0, uv).x,
             texture(iChannel0, uv + vec2(0.0, 0.125)).x -
             texture(iChannel0, uv).x);
    uv = uv + 0.05 * grad;
    uv.x = 1.0 -uv.x;
	fragColor = texture(iChannel1, uv);
 //   fragColor = vec4(vec2(0.5) + grad, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 outcolor = vec4(0.0);
    for (float i = 0.5; i < 4.0; i += 1.0) {
    vec2 sampleFrom = 
        (fragCoord / iResolution.xy) +
        vec2(0.0, i + 4.0) / iResolution.xy;

    outcolor = outcolor + 0.25 * mix (texture(iChannel0, sampleFrom),
                     texture(iChannel1, vec2(sampleFrom.x, 1.0)),
                     step(1.0, sampleFrom.y));
    }
    fragColor = max(outcolor, 0.125);
}
