void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int iter = 1000;

    float zoom_factor = 1.0/(iTime/10.0);

    float offset = -0.0 + (1.0/zoom_factor);

    vec2 c, z;

	vec2 uv = fragCoord.xy / iResolution.xy;

    c.x = (5.0/3.0) * (uv.x - offset) * zoom_factor;

    c.y = (uv.y - 0.5) * zoom_factor;

    z = c;

    int outI;

    for(int i=0; i<iter; i++)
    {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        outI = i;

        if((x * x + y * y) > 4.0) break;

        z.x = x;
        z.y = y;
    }

    if (outI == iter)
    {
        fragColor = vec4(0,0,0,1);
    }
    if (outI != iter)
    {       
    	fragColor = vec4(outI/100,outI/100,outI/100,1);
    }
}
