float stripe(vec2 uv){
    float speed = 40.;
    float noLines = 100.;
    float str = .6;
    float w = sin((uv.y+(iTime/speed))*noLines);
    w = w*str;
	return w-.2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texColor = texture(iChannel0,uv);

    fragColor = vec4(texColor.b, texColor.gr+stripe(uv),1.0);

}
