{
	fragColor = pow( texture(iChannel0, fragCoord/iResolution.xy), vec4(1.6) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 tuv = fragCoord/iResolution.xy,
        uv = tuv - .5,
        muv = uv;

    for (int i = 0; i < 7; i++) {
        float t = float(i)+iTime*(1.+float(i))*.1;
        muv = abs(muv)*mat2(sin(t),cos(t),-cos(t),sin(t));
    	muv = abs(muv)-(float(i)*.1);
    }


    float sx = mod(abs(atan(muv.y,muv.x))/9.-iTime*.04, .5);
    float freq = max( texture(iChannel1, vec2(sx,0.)).x,
                     texture(iChannel1, vec2(sx,.25)).x );
    freq *= 1.+sx*2.;

    float line = (length(max(abs(uv), 0.))-freq*.3)*10.;
    line = max(0., line-max(0.,line-1.)*2.);
	line *= line;

    vec3 c = texture(iChannel3, floor(abs(muv*14.)-iTime)/64.).xyz*
                 texture(iChannel3, floor(abs(muv*14.+freq)-iTime)/64.).x;

    c = c+c*line+line;

    fragColor = vec4(texture(iChannel0,uv*(.98+freq*.05+cos(iTime*.324+freq*7.342)*.005*freq)+.5).x*.9,
                     texture(iChannel0,uv*(.98+freq*.05+cos(iTime+.294+freq*12.342)*.005*freq)+.5).y*.9,
                     texture(iChannel0,uv*(.98+freq*.05+cos(iTime*.794+.593+freq*3.1324)*.005*freq)+.5).z*.9,
                     1.)+
        		vec4(c, 1.)*.1;
}
