void mainImage( out vec4 O, in vec2 f ) {
    vec2 uv = f.xy / iResolution.xy - 1.;
    vec3 g =  mix(vec3(215, 95, 67), vec3(150, 196, 168), f.y / iResolution.y) / 255.;   
    vec3 n = texture( iChannel0, 1. + uv, 7.0 * abs(1. + uv.y - .5) ).xyz;
    vec3 m = mix(n, g, f.y / iResolution.y);
    float x = (uv.x + 4. ) * (uv.y + 4. ) * iTime * 10.;
    O = vec4(m*(g*.8)*2.,1.) + (mod((mod(x, 13.) + 1.) * (mod(x, 123.) + 1.), .01)-.005) * 5.;
    if( uv.x > fract( abs( sin(iTime * .2) ) ) - .5 * 2.) O = texture(iChannel0, 1. + uv);
}
