const vec4 color = vec4(0.3, 0.8, 0.7, 1.0);



// Complex multiplication
vec2 mult(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 f(vec2 z, vec2 inv_k) {
    z.x += -2.0*float(z.x >= 0.0) + 1.0;
    return mult(z, inv_k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 k = vec2(0.45+0.05*cos(iTime*0.123), 0.5+0.1*sin(iTime*1.0));
	vec2 inv_k = vec2(k.x, k.y)/(length(k)*length(k));

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 z = vec2(uv.x-0.5, uv.y-0.5)*1.0;

	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    int I = 0;
    for(int i = 0; i < 300; i++) {
        z = f(z, inv_k);

        if(dot(z,z) > 1e5) {break;}
        I = i;
    }

    fragColor = color*float(I)/200.0;
}
