void mainImage( out vec4 O, in vec2 f ) {
	vec2 uv = (f.xy / iResolution.xy) * 1. - 1.;
    vec4 s = texture(iChannel0, vec2(fract(iTime * .01), 1. + uv.y));
    vec4 n = texture(iChannel0, uv + 1.);
    vec2 d =  (iResolution.x / 8.) * (1. / iResolution.xy);
    vec4 p = texture(iChannel0, d * floor(uv / d));
    O = (vec4(1., .2, 0., 1.) * n + s * .5) + p * .3;
}
