float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(iTime*0.25,iTime/10.))) * mod(99.53*iTime/200.,10.));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //shift so 0 centered
    uv=uv-(0.5,0.5);

    float x=uv[0];
    float y=uv[1];


    vec3 col=vec3(0,0,0);

    if(x*x+y*y+0.105*abs(x)<0.07*rand(uv)){

        float val=(0.71*rand(uv))-(x*x+y*y+0.105*abs(x));
        col = 0.55*exp(val)*vec3(1,0.7,0.2);


    }
    else{
        col=vec3(0,0.0,0.1);
    }



    // Time varying pixel color

    // Output to screen
    fragColor = vec4(col,0.0);
}
