renderpass.code:
vec2 conv(vec2 fragCoord)
{
    return (fragCoord-iResolution.xy/2.0)/min(iResolution.x, iResolution.y);
}
vec2 conv1(vec2 xy)
{
    return xy*min(iResolution.x, iResolution.y)/iResolution.xy + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = conv(fragCoord);
    float l = length(uv);

    float theta = iTime;	// blink effect
//    theta = (1.0-l) * 0.1 + iTime * 0.1; // wow
    theta = (1.0-l) * 6.0 + iTime * 3.0; // small waves
//    theta = l * 0.1 + iTime * 0.2;	// tunnle like a black hole
//    theta = l * 40.0 + iTime * 2.0; // small waves to join one point

    vec2 pos = vec2(cos(theta), sin(theta));
    fragColor = texture(iChannel1, conv1(pos));
}

vec4 someFunction( vec4 a, float b )
{
    return a+b;
}
float len2(float dx, float dy)
{
    return dx*dx + dy*dy;
}
