void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 A = vec3(sin(iTime*0.416),cos(iTime*0.728),sin(cos(iTime)));
	vec3 B = vec3(1) - A;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx;

    uv = uv * 4.5;
    uv = uv - vec2(2);

    //uv = uv.yx;

    int n = 600;

    uv = mandel(uv, n, 0.1);

    float d = uv.x + uv.y;
    d += iTime*0.1;
    d = sin(mod(d, 1.0)*3.1415);

    // Time varying pixel color
    vec3 col = mix(A, B, d);


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 mandel(vec2 c, int iter, float multiplier){
    vec2 z = vec2(0);

    for (int i = 0; i < iter; i++) {
        //set a and b from the current z value
        float a = z.x;
        float b = z.y;

        //perform calculation for this iteration
        z = vec2(
            (a * a) - (b * b) + c.y,
            (2.0 * a * b) + c.x
        );

        //check for out of bounds
        if (length(z)*length(z) > float(1 << 16)) {
            float smth = float(i + 1) - log(log(length(z))) / log(2.0);
            return vec2(smth * multiplier);
        }
    }
    //edge case to return zero if never escapes
    return vec2(0);
}
