vec2 rep(vec2 p, vec2 m) {
	return mod(p,m);   
}
float sdCircle(vec2 p, float r) {
	return length(p) - r;
}
float asFilled(float d) {
		return (smoothstep(0.,.01,d));
}
vec2 mov(vec2 xy, vec2 t) {
	return xy - t;
}
vec2 scale(vec2 xy, vec2 s) {
	return xy.xy/s.xy;
}
vec2 rot(vec2 xy, float ang) {
	float s = sin(ang);
    float c = cos(ang);
    return xy * mat2(c, -s, s, c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = vec2(iResolution.x/iResolution.y, 1.);
    vec2 uv = (fragCoord/iResolution.xy *2. - vec2(1.0)) * aspect;
    vec4 m = vec4((iMouse.xy / iResolution.xy*2.0 - vec2(1.0)) * aspect,(iMouse.zw / iResolution.xy*2.0 - vec2(1.0)) * aspect);
    vec3 coloure = vec3(0.5*sin(iTime)+0.5,mod(iTime,1.),0.5*sin(iTime)+0.5);
    vec2 x = rep(uv, vec2(0.5,0.5)); 
    vec2 a = mov(x, vec2(0.25,0.25));
    vec2 h = scale(a, vec2(sin(iTime)+2.)*.1);
    vec2 s = rot(h, iTime);
    float d = sdCircle(h, 0.7 + sin(atan(s.x,s.y)*10.)*.1);
    float final = asFilled(d*sin(iTime)); 
	fragColor = vec4(final*coloure, 1.);
}
