void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 108.0;
    float t = iTime;
    float bt = t * bpm * 3.1415926535 * 2.0;

    fragColor.x = cos(bt);

    vec3 col0 = vec3(0.9, 0.6, 0.3);
    vec3 col1 = vec3(0.9, 0.7, 0.3);

    fragColor.xyz = col0 * 0.5;
    fragColor.xyz += col1 * pow(abs(cos(bt * 0.5)), 3.0) * 0.3;

    vec3 col2 = vec3(1.0, 1.0, 1.0);

    vec2 nuvd = nuv * vec2(cos(bt * length(nuv)), sin(bt * length(nuv) * 0.2));

    fragColor.xyz += col2 * (1.0 - length(nuvd));

    float s0 = texture(iChannel1, uv * 0.05 + vec2(sin(bt / 32.0) * 0.5, sin(bt / 96.0) * 0.2)).x;
    s0 = pow(s0, 2.0);
    s0 = step(pow(s0, 4.0), 0.001 * step(fract(bt), 0.1 * sin(bt) * length(nuv)));
    s0 = s0 * pow(texture(iChannel0, vec2(0.1, 0.25)).x, 2.0) * 4.0;

    fragColor.xyz += col2 * s0 * 0.2;
}
