const float rings = 45.0;	//exactly the number of complete white rings at any moment.
const float velocity=91.;	
const float b = 0.003;		//size of the smoothed border


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = fragCoord.xy/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
	position.x *= aspect;
	float dist = distance(position, vec2(aspect*0.5, 0.001));
	float offset=iTime*velocity;
	float conv=rings+5.0*sin(iTime*0.35)*12.;
	float v=dist*conv-offset;
	float ringr=floor(v);
	float color=smoothstep(-b, b, abs(dist- (ringr+float(fract(v)>0.5)+offset)/conv));
	if(mod(ringr,2.)==1.)
		color=1.-color;

	vec2 uv = fragCoord.xy / iResolution.xy;	
	vec3 obump = texture(iChannel0, uv).rgb;
	float displace = dot(vec3(color,color,color), vec3(0.3, 0.6, 0.1));
	displace = (displace - 0.5)*sin(iTime*0.951)*sin(iTime*0.0085)*0.05+0.01;



	vec4 face = texture(iChannel0, uv + vec2(displace));
	vec4 final = mix(face,vec4(color,color,color,1.0),0.0);
	fragColor = final;
}
