void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p = uv;

	vec3 col = vec3(0.0, 0.0, 1.0);
	fragColor = vec4(col, 1.0);

	float lineas = sin((p.x*50.0));

	lineas =sqrt(lineas + tan((uv.y)*25.0) + exp(lineas));
	fragColor.y = lineas; 

	lineas = -exp(65.0 + (p.x*25.0 + p.y*10.0));
	fragColor.x = lineas;

}
