const float si = 5.; // sampling quality for sharpening, kind of 


vec4 colorAdjust(vec4 col) { 

    // simulate film colors
	col = 		vec4(col.x*0.8+col.y*0.2,
					 col.x*0.2+col.y*0.6+col.z*0.2,
					 col.y*0.7+col.z*0.3,1);	

    // increase saturation
    float sat = 1.0;
    float o = 1. + sat;
    float z = -sat / 2.;
	col = 		vec4(col.x*o+col.y*z+col.z*z,
					 col.x*z+col.y*o+col.z*z,
					 col.x*z+col.y*z+col.z*o, 1);	                     
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.5;
    float contrast = 0.3;

    if(length(iMouse.xy) > 0.) {
        contrast = iMouse.y/iResolution.y;
        radius = iMouse.x/iResolution.x;
    }

	float muln= 1. / pow((si)*2.,2.);
	float blurs = pow(radius,5.)/(si*2.);

	vec2 uv = vec2(iResolution.x-fragCoord.x, iResolution.y - fragCoord.y) / iResolution.xy;
	vec4 color = vec4(0);
	for(float x = -si+0.5 ; x < si; x++) {
		for(float y = -si+0.5 ; y < si; y++) {
			vec4 col = (texture(iChannel0, uv + blurs*vec2(float(x),float(y))) );
			color = color + col;
  		}
	}
    vec4 org = texture(iChannel0, uv);
    vec4 sharpenedImage = org - color * muln;
	fragColor = colorAdjust(sharpenedImage*0.005*(20.*pow(contrast,3.))/blurs + 0.3*org + 0.3);
}
