void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float width = .01;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 pixel = texture(iChannel0, vec2(uv.x, uv.y));
	vec4 right = texture(iChannel0, vec2(uv.x+width, uv.y));
	vec4 up = texture(iChannel0, vec2(uv.x, uv.y+width));
	float red = abs(pixel.r-right.r)+abs(pixel.r-up.r);
	float green = abs(pixel.g-right.g)+abs(pixel.g-up.g);
	float blue = abs(pixel.b-right.b)+abs(pixel.b-up.b);
	vec4 edges = vec4(red,green,blue,1.);
	fragColor = edges;
}
