float dist(vec2 r, vec2 c)
{	
	return length(r-c);
}

float triangle(float t, float a)
{	
	return abs(2.0*(t/a-floor(t/a+0.5)));
}

float poiseuille(float r, float R)
{	
	return (1.0 - (r/R)*(r/R));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x*=16.0/9.0;

	float d, tt, tt2;
	float Radius = 0.0125;
	float c = 1.0;
	float s = 0.05*sin(uv.x+iTime);

	c*=smoothstep(0.25,0.256,uv.y+s);
	c*=(1.0-smoothstep(0.75,0.756,uv.y+s));


	for(float kulka=0.0; kulka<1.0; kulka+=0.01)
	{
		tt = texture( iChannel0, vec2(kulka,0.02) ).r;	
		tt2 = texture( iChannel0, vec2(kulka,0.2) ).r;	

		d=dist(uv, vec2(2.0*tt, 0.25+0.5*tt2-s));
		if(d < Radius)
				c*=smoothstep(Radius*0.8,Radius,d);
	}


	fragColor = vec4(vec3(c,c,c),1.0);
}
