/*
	Title: Simple Sierpinski fractal
	Tags: fractal
	Description: Sierpinski triangle created with the bitwise AND operation.
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = min(iResolution.x, iResolution.y);
    vec2 p = fragCoord.xy - (iResolution.xy - size) / 2.0;

    vec3 col = vec3(0.0);
    if(p.x >= 0.0 && p.y >= 0.0 && p.x < size && p.y < size) {
        vec2 z = p / exp2(fract(iTime));
    	uint res = uint(z.x) & uint(z.y);

        vec3 fg = vec3(1.0, 0.0, 0.0);
        vec3 bg = 1.5 * vec3(0.0, 0.0, 1.0) / log2(float(res));
    	col = res == 0U ? fg : bg;
    } 

    fragColor = vec4(col,1.0);
}
