mat2 rotate (float t) {
    return mat2 (cos(t),sin(t),
           sin(t*2.0), cos(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
	vec2 uv = (fragCoord.xy / iResolution.xy)*rotate(t);
    float sound = texture( iChannel0, vec2(uv.x,1.0)).x;
    vec3 col = vec3(1.0/(abs(fract((uv.x)*10.0))*(190.0*sound)))+vec3(1.0/(abs(fract((uv.y*sound)*(10.0*sound)))*150.0*sound));
	fragColor = vec4(col,1.0);
}
