void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (1.3*fragCoord.xy - 0.5*iResolution.xy)/min(iResolution.y, iResolution.x)*1.8;
    uv.y -= 0.75;
    uv.x -= 0.5;

    //backcolor
    vec3 bcol = vec3(0.4,0.2,0.4-0.25*uv.y)*(2.0-length(uv)/1.5);

    //time
    float tt = mod(iTime,1.5)/1.5;
	float ss = pow(tt,.2)*0.5 + 0.5;
	ss = 1.0 + ss*0.5*sin(tt*6.2831*3.0 + uv.y*0.5)*exp(-tt*4.0);
	uv *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);

    //shape
    float a = atan(uv.x,uv.y)/3.141593;
	float r = length(uv);
	float h = abs(a);
	float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

    //heartcolor
	float s = 1.0-0.5*clamp(r/d,0.0,1.0);
	s = 0.75 + 0.75*uv.x;
	s *= 1.0-0.25*r;
	s = 0.5 + 0.6*s;
	s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );
    vec3 hcol = vec3(1.0,0.5*r,0.3)*s;

    vec3 col = mix( bcol, hcol, smoothstep( -0.11, 0.11, d-r) );
    fragColor = vec4(col,1.0);
}
