void mainImage( out vec4 O, vec2 I )
{
	I /= iResolution.xy;
    I.x = 3. * fract( I.x + iDate.w / 9. );
    float d = fract( I.x );
    vec3 c = vec3( 1, d, 1. - d );  //  c = c.xzy for continuous color palette
    for ( float i = 1. ; i < 3. ; i++ ) {
        if ( i > I.x ) break;
        c = c.yzx;
    }    
    O = vec4( c * .5 * ( 1. + cos( 2.5 * I.y ) ), 1 );
}
