#define TAU 6.28318530718

#define count 12.0
#define speed 8.0  // center rotation time in seconds.
#define margin 0.1
#define phase (3.0*TAU/4.0);

vec4 GetColor(in vec2 fragCoord)
{
    vec2 center = iResolution.xy / 2.0;
	vec2 uv = 2.0*(fragCoord.xy-center) / min(iResolution.x, iResolution.y);

    float luv = length(uv);
    float fd = luv * count;
    float d = ceil(length(uv) * count);
    float dm = mod(fd, 1.0);

    float t = mod(iTime*TAU/speed, TAU);

    float a = d*t+phase;
    vec2 dir = vec2(cos(a),sin(a));

    float v = 1./count;
    float k = smoothstep(.0,1.,abs(mod(luv+v*.5, v)-v*.5)/.01);
    return vec4(luv <= 1.0 ? 1.-k*smoothstep(.0,.01,dot(uv,dir)) : 1.);	
    /*
    float k = smoothstep(1.0,0.0,abs(dm-.5)*2.0)*smoothstep(0.0,1.0,(dot(uv,dir)/(luv*luv*luv)));
    return vec4(luv <= 1.0 ? 1.-k : 1.);
*/
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = GetColor(fragCoord);
}
