const float n = 3.0;
const float size = 0.15;
const float angle = 0.75;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x;

    float ac = acos(normalize(uv).y) * n / 3.1415926535;
    ac = trunc(ac * 0.5 + 0.5) / n * 2.0 * 3.1415926535;

    vec2 dir = vec2(sin(ac) * sign(uv.x), -cos(ac));

    float time = 1.0 - fract(iTime * 0.5);

    vec2 temp = uv * vec2(1.0, -1.0) - time * dir;
    float siz = step(dot(temp, dir), size);
    float ang = step(angle, dot(normalize(temp), dir));

    fragColor = vec4(ang * siz *  (1.0 - time));
}
