#define TWO_PI 6.28318530718

float circle(in vec2 _st, in float _radius){
    vec2 l = _st-vec2(0.5);
	return .2-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*2.0),
                         dot(l,l)*32.0);
}

//  Function from Alien IQ  
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0, 
                     0.0, 
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x = fragCoord.x / iResolution.y;

    float ts=texture(iChannel0,-uv).x;

    vec3 tc=texture(iChannel1,uv+vec2(iTime*ts/1000.,0.0)).rgb;

    vec2  Center = vec2(1.0)-uv;
    float angle = atan(Center.x,Center.y);
    float radius = length(Center)/1.2;


   vec3  color = hsb2rgb(vec3(circle(uv-vec2(0.5,0.0),ts*max(0.6,2.0*ts))));
    color  -= -hsb2rgb(vec3((angle/TWO_PI)-iTime/5.,radius,1.0));

	fragColor = vec4(tc*color*2.0,1.0);
}
