#define PI 3.14159265359

const vec3 colorA = vec3(0.89,0.51,0.112);
const vec3 colorB = vec3(1.,0.933,0.224);

float shape(vec2 pos, float rad) {
    vec2 uv = gl_FragCoord.xy;
    uv.x = uv.x*2. - iResolution.x*.5;

    float weird = rad * abs(sin( iTime + (1.-pow(abs(uv.x-(iResolution.x*.5))*.02, 2.))));
    float d = 1. - (length(pos - uv) - weird);
    //d *= uv.y*.001;

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 color = vec3(0.0);

    vec2 uv = fragCoord.xy;
    vec2 st = uv/iResolution.xy;

    float x = st.x*2. - 1.;
    float y = 1. - pow(abs(sin(PI*x/2.)),4.5);

    float pct = clamp(abs(sin(iTime+y))*y, 0., 1.);

    vec2 pos = vec2(iResolution*.5);
    float rad = iResolution.y*.4;
    float mask = shape(pos, rad);
    mask *= .25;

    // pct = clamp(pct, 0., 1.);
    // mask = clamp(mask, 0., 1.);

    // color = mix(colorA, colorB, pct);
    mask = (mask)*pct;

    //mask = normalize(mask);

    color = mix(colorA, colorB, mask);

    fragColor = vec4(color,1.0);
}
