void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 screen = fragCoord.xy / iResolution.xy; // <== environnement
	float sound = texture( iChannel0 , vec2(screen .x , 0.75)) .x; // <== chargement de l'image
    vec3 color = vec3 (.0); // <== initialisation

    color += vec3(abs(sin(iTime)) - smoothstep(0.0 , 0.02, abs(sound - screen.y)),
                  abs(sin(iTime * 2./3. *3.1415)) - smoothstep(0.0 ,0.02, abs(sound - screen.y)),
                  abs(sin(iTime * 4./3. *3.1415)) - smoothstep(0.0 ,0.02, abs(sound - screen.y)));

    fragColor =vec4 (color, 1. );


}
