void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.xy-0.5 * iResolution.xy);

  uv =  uv.xy / (iResolution.y);

  vec2 a = floor(fragCoord); 
  vec2 b = fract(fragCoord);

  if(mod(a.x + a.y, 2.0 ) > 0.0 && distance(uv, vec2(0.0, 0.0)) > 0.4)
  {
	discard;
  } else 
  {
    vec4 color = texture(iChannel0, fragCoord.xy / iResolution.xy);
    fragColor = color; 
  }
}
