void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


	vec3 wave_color = vec3(0.0,0.0,0.0);

	// To create the waves
	float wave_width = 1.0;
	uv  = -1.0 + 2.2 * uv;
	uv.y += 0.1;
    float r,g,b;
    r = 0.0;
    g = 1.0;
    b = 0.0;
	for(float i = 0.0; i < 15.0; i++) {

		uv.y += (0.06 * sin(uv.x + i/7.0 + iTime ));
		wave_width = abs(1.0 / (107.0 * uv.y));
		wave_color += vec3(wave_width * r  , wave_width * g, wave_width * b);
	}



	fragColor = vec4(wave_color, 0.5);
}
