// fixed for mobile + safari
void mainImage(out vec4 o, vec2 O)
{
    float t = iTime,a=-t*.05,s=sin(a),c=cos(a);
    vec2 R = iResolution.xy,
        V=(O-.5*R)/R.y*5.*mat2(c,s,-s,c)
        ,N=O/R-.5;
    o=.2+fract(t*.5+abs(vec4(N,-N)*.2+V.x)+abs(V.y))
        -.2*fract(sin(dot(R+t,N))*1e5)
        -dot(N,N*3.)
        ;
}




