#define time iTime*3.0

float noise(float x)
{
    return (sin(x * 1.0 + time * 1.0) + sin(x * 2.3 + time * 1.5) + sin(x * 3.3 + time * 0.4)) / 3.0;
}

float fractal(float x)
{
    float f = 0.0;
    f  = 0.50000*(noise( x )); x = x*2.01;
    f += 0.25000*(noise( x )); x = x*2.02;
    f += 0.03125*(noise( x )); x = x*2.05;
    return f;
}

float norm(float x)
{
    return x * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 5.0;

    float h = norm((fractal(uv.x) + fractal(uv.y)) / 2.0);

    // Time varying pixel color
    vec3 col = vec3(h);

    // Output to screen
    fragColor = vec4(col,1.0);
}
