void mainImage( out vec4 z, vec2 I ) // FabriceNeyret2's codegolfed 173chars variant
{
    I = 1.75* ( I+I- (z.xy= iResolution.xy) ) / z .y;
    I.x++;
    z -= z;
    for ( float n = 0.; n < 1.; n += .01 )
        if ( length( z.xy = I/dot(I,I) + mat2(z.xy,-z.y,z.x)*z.xy ) > 2. ) z += n;
}

