void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speedFactor = 1.0 + sin(iTime) / 2.0; 
    float x = pow(50.0, speedFactor);
	vec2 circle = vec2(x, 50);
    float circleRadius = 20.0;
    if(distance(fragCoord, circle) > circleRadius) {
    	fragColor = vec4(1.0);
    }

    else {
    	fragColor = vec4(0.0);
    }
}
