// https://soundcloud.com/gazelleh-ghazall/h3llo-anevo-remix-final-mp3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;
	// first texture row is frequency data
    float p = 0.1*atan(abs(uv.x-0.5),-0.2+uv.y);
	float fft  = texture( iChannel0, vec2(p,0.25) ).x; 

    // second texture row is the sound wave
	float wave = texture( iChannel0, vec2(uv.x,0.75) ).x;

	// convert frequency to colors
	vec3 col = 0.9*vec3( fft, 3.3*fft*(1.0-fft), 1.0-fft ) * 1.5*fft;

    // add wave form on top	
	col += 0.6 - ( smoothstep( 0.0, 0.25, abs(wave - uv.y - 0.3) ));

	// output final color
	fragColor = vec4(col,1.0);
}
