#define PI 3.14159265359

vec4 logo(in vec2 uv, float time) {
    float rad = atan(-uv.y, uv.x) + time * 0.1;
    float r = length(uv * smoothstep(0.0, 1.0, time)) - tan(rad * 3.0) * 0.1;
    float rayv = fract((rad - sin(r * 64.0 + time * 4.0) * r / 32.0) / PI * 16.0);
    float ray = smoothstep(0.45, 0.5, rayv) * smoothstep(0.9, 0.85, rayv);
    float circle = smoothstep(1.0, .2, r);     
    float circle2 = smoothstep(0.2, 0.4, r);
    float radiance = 0.6 + 0.4 * fract((1.0 - r*r) * 16.0 - time);
    return circle * circle2 * radiance * (mix(vec4(0.278, 0.733, 0.831, 1.0), vec4(0.145, 0.541, 0.627, 1.0), ray));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = fract(iTime / 4.0) * 5.0;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y * (1.0 + smoothstep(4.0, 5.0, time) * 2048.0);
    fragColor = logo(uv, time) / dot(uv, uv) / 4.0 * smoothstep(0.0,1.0,time) * (1.0 - smoothstep(3.5, 4.5, time));

}
