const float interval = .8;
const float cycle = .5;
const float gradient = 2.;
const float speed = .8;

const float angle = radians(135.);

const vec3 color = vec3(0.3, 1., 0.2);

#define RADIAL 0
#define SOLID 0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.x;

    float r = angle;
    vec2 dir = normalize(vec2(sin(r), cos(r)));
    vec2 p = vec2(0.5, 0.5 * (iResolution.y / iResolution.x));

    float m = gradient;
    float b = mod(iTime * speed, interval);

#if RADIAL
    float x = -distance(uv, p);
#else 
    float x = -dot(uv, dir);
#endif

    float y = (m * x) + b;
    y = mod(y, interval) * (1./cycle);

#if SOLID
    y = round(y);
#endif

    // Output to screen
    fragColor = vec4(color - y, 1.0);
}
