vec2 complex_mul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;

    float time = iTime * .3;

    vec2 center = .7785 * vec2(cos(time), sin(time));

    float zoom = .35; 

    vec2 z = (uv - vec2(.85, .5)) / zoom;
    float iterations = 40.;

    bool is_out = false;
    float i = 0.;
    for(i=0.;i<iterations && !is_out;i++) {
        z = complex_mul(z, z) + center;
    	is_out = length(z) > 2.;
    }

    vec3 color = vec3(0,0,0);
    if(is_out)
        color = vec3(1, 1, 1) * i / float(iterations);

    fragColor = vec4(color,1.0);
}
