void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec2 xy = fragCoord.xy; // Iegustam pashreizeja pikselja koord.

xy.x = xy.x / iResolution.x; // Sadalam koordinates ar ekrana izmeru
xy.y = xy.y / iResolution.y;

// Pikselis visvairak pa kreisi 0,  pikselis visvairak pa labi 1
vec4 solidGreen = vec4(0,0.0,0.0,1.0); 

vec4 texColor0 = texture(iChannel0,xy); 
vec4 texColor1 = texture(iChannel1,xy);

if(xy.x > 0.5) {

solidGreen.b = 1.0;
fragColor = solidGreen;

}    
else {

fragColor = texColor0;

if ( (texColor0.r + texColor0.b) < texColor0.g ) {

fragColor = texColor1;

}

}

}
