void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 2.0) - 1.0;
    vec3 color = vec3(0.0);

    float distance = abs(uv.y - sin(15.0*uv.x + iTime) / 2.0);
    float margin = 0.5;
    if(mod(uv.x, 0.3) < 0.15) {
        margin = 0.8;
    }
    if(distance < margin) {
        float v = exp(-4.0*distance) + 0.3 * sin(iTime*5.0);
        color =  (1.0 - distance / margin) * v * vec3(0.6, 0.1, 0.124);
    }

	fragColor = vec4(color, 1.0);
}
