//forked from https://www.shadertoy.com/view/llfSzH


float mic(float i, float j){
 return texture(iChannel0,vec2(i,j)).r;   
}
void push(inout vec2 p, float i){
    float a = atan(p.y,p.x);
    float d = length(p.xy);
    d+=d*i;
    p.y = sin(a)*d;
    p.x = cos(a)*d;
}

void mainImage( out vec4 f, vec2 u )
{
    float s = 1.;
    vec3 r = vec3(s*.5, iTime*.1,0.) + s*.5,
         R = iResolution ;

    u-= R.xy*.5;
    float d = length(u/R.y)*2.;
    float a = sin(iTime*3.1415*.0125);
    u*= mat2(d,a,-a,d);
    u+=R.xy*.5;
    vec2 v = iMouse.xy/iResolution.xy;
    v = 1.0-v;
    v.y*=4.;

    for( float i = .5; i > .0 ; i-=.015 ) {
        float m = mic(mod(i*2.,v.x),.0)*v.y;
        f.a = length( mod(r,s) - (s*.5) ) - .2;
        r += vec3( (u+u-R.xy)/R.y, 2. ) * (.2)*f.a; 
        ;
        r.xz -= vec2(s*.5);
        push((r.xz),m*.1);
        r.xz += vec2(s*.5);

        f.r=abs(sin(i));
        if( f.a < .001 ) break ;
    }
    f.rgb = vec3(pow(f.r*2.5,1.)); 
}
