const int iter = 100;
const float stepTime = 0.1;
const float scale = 0.5;
const vec2 center = vec2(0.5, 0.0);

const vec3 color1 = vec3(1.0, 0.0, 0.0);
const vec3 color2 = vec3(0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor,  vec2 uv ) {
	uv /= iResolution.xy;
	vec2 c = vec2(4,3) * (uv-.5) / (scale * 3.0) - center, z=c;

    fragColor -= fragColor;

    for(float i=0.; i< (float(iter) / 100.0); i+=.01) 
        if( length(z = mat2(z,-z.y,z.x) * z + c) > 2.) { 
            fragColor += 1.0 - i;
            break;
        }

}
