#define S 5. // Scale
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float music = texture(iChannel0, vec2(0.0, 0.01)).x;
    vec2 p = ((( fragCoord.xy / iResolution.xy)-0.5) * vec2(iResolution.x/iResolution.y,1.)) + music  ;
    float circle = clamp(1.-(length(p-0.5)-music)*iResolution.y,0.,1.)  -  clamp(1.-(length(p-0.5)-0.5)*iResolution.y,0.,1.);
    vec2 uv = floor(S * p.xy * vec2(iResolution.x / iResolution.y, 1) / iResolution.xy);
	vec4 cool = vec4 (uv,0.5+0.5*sin(iTime),1.0);
	fragColor = mix(vec4(vec3(mod(uv.x + uv.y, 2.)), 1.0), cool, vec4(vec3(circle), 1.0));
}
