void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 39.0+38.0*sin(iTime*0.5);
    vec2 p = fragCoord.xy / iResolution.xy;

	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);

    vec2 uv1 = (uv-vec2(0.9,0.5))*zoom;
    vec2 uv2 = (uv-vec2(0.9,0.5))*zoom + vec2(0.5,0.5);
    vec2 uv3 = (uv-vec2(0.9,0.5))*zoom*0.02;

    float f = texture( iChannel1,uv3 ).r;
    f = clamp((f-.5)*8.,0.,1.);

    vec4 tex1 = texture(iChannel0, uv1 );
    vec4 tex2 = texture(iChannel0, uv2 );

	fragColor = (tex1*f+tex2*(1.-f));
    if(uv.x > 0.9)
    {
        fragColor = tex1;
    }
    fragColor += 1.-smoothstep( 0.0, 0.005, abs(uv.x-0.9) );

}
