void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - vec2(1.0);
	uv.x *= iResolution.x / iResolution.y;

    float p = 1.0 + 3.0 * iMouse.x / iResolution.x;
    float l = pow(abs(uv.x), p) + pow(abs(uv.y), p);

    if(l < 1.0) {
		fragColor = vec4(l);
    } else {
        fragColor = vec4(0.0);
    }
}
