void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 fColor;

    // get each pixel coordinates in clip space:
    vec2 V = fragCoord.xy / iResolution.xy;
    V = V * 2.0 - 1.0;

    // compute light vector at pixel coordinate center:
    vec4 L = vec4(1.0, 1.0, 1.0, 1.0) - vec4(V.xy, 0.0, 1.0);

    vec4 N = vec4(0.0, 0.0, 1.0, 1.0);
    vec4 ambient = vec4(0.1, 0.1, 0.1, 1.0);
    vec4 diffuseProduct = vec4(0.0, 1.0, 0.0, 1.0);

    // normalize before dot product:
    L = normalize(L);
    N = normalize(N);
    float Kd = max( dot(L, N), 0.0 );
    vec4  diffuse = Kd*diffuseProduct;

    fColor = ambient + diffuse ;
    fColor.a = 1.0;

    fragColor = fColor;
}
