void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / max(iResolution.x, iResolution.y);
    vec2 m = vec2(1.0, iResolution.y / iResolution.x) / 2.0;
    float t = (iTime + 100.0) / 4.0;
    float s = 0.01 + 0.99 * min(texture(iChannel0, vec2(1.0, 0.0)).x * 1.5, 1.0);
    float s2 = 0.99 + 0.01 * s;
    vec2 p = vec2(
        m.x + sin(t * 4.0 + s * 4.0) * m.x * s,
        m.y + cos(t * 4.0 + s * 4.0) * m.y * s);
    float d = distance(uv.xy, p) * 20.0 * s;
	fragColor = vec4(
        0.7 + 0.3 * sin(d * sin(t * s2 * 1.0) * 16.0),
        0.7 + 0.3 * sin(d * sin(t * s2 * 1.5) * 4.0),
        0.7 + 0.3 * sin(d * sin(t * s2 * 2.3) * 2.0),
        1.0);
}
