const float global_speed =    2.;
const float weight11_speed =  14./13.*global_speed;
const float weight12_speed =  20./19.*global_speed;
const float weight21_speed =  38./37.*global_speed;
const float weight22_speed =  24./23.*global_speed;
const float step1_speed =     3.*8./ 7.*global_speed;
const float step2_speed =    2.*12./11.*global_speed;
const float step3_speed =    18./17.*global_speed;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    

    float weight0 = 1.;
    float weight1 = sin(iTime * weight11_speed) + sin(iTime * weight12_speed);
    float weight2 = (sin(iTime * weight21_speed) + sin(iTime * weight22_speed))/2.;
    float weight3 = 1. - weight1 - weight2 - weight0;

    float step0 = 0.;
    float step1 = sin(iTime * step1_speed)+2.;
    float step2 = sin(iTime * step2_speed)*2.+3.;
    float step3 = sin(iTime * step3_speed)*2.+5.;

    vec4 pixel0 = texture(iChannel0, uv, step0) * weight0;
    vec4 pixel1 = texture(iChannel0, uv, step1) * weight1;
    vec4 pixel2 = texture(iChannel0, uv, step2) * weight2;
    vec4 pixel3 = texture(iChannel0, uv, step3) * weight3;

    fragColor = pixel0 + pixel1 + pixel2 + pixel3;
}
